import React from 'react'; // Nhập React từ thư viện React
import { Link } from 'react-router-dom'; // Nhập Link từ react-router-dom để điều hướng giữa các trang trong ứng dụng

const Header = () => {
  return (
    <header style={headerStyle}> {/* Thẻ header bao bọc toàn bộ thanh header với style từ headerStyle */}
      <div style={logoContainerStyle}> {/* Div bao chứa logo, áp dụng style logoContainerStyle */}
        <Link to="/" style={logoStyle}> {/* Liên kết đến trang chủ, áp dụng style logoStyle */}
          <h1>My E-Commerce</h1> {/* Tiêu đề chính (logo) của trang web */}
        </Link>
      </div>
      <nav style={navStyle}> {/* Thẻ nav chứa các liên kết điều hướng với style navStyle */}
        <ul style={navListStyle}> {/* Danh sách các mục trong menu điều hướng */}
          <li style={navItemStyle}><Link to="/" style={linkStyle}>Home</Link></li> {/* Liên kết đến trang chủ */}
          <li style={navItemStyle}><Link to="/products" style={linkStyle}>Products</Link></li> {/* Liên kết đến trang sản phẩm */}
          <li style={navItemStyle}><Link to="/cart" style={linkStyle}>Cart</Link></li> {/* Liên kết đến trang giỏ hàng */}
          <li style={navItemStyle}><Link to="/about" style={linkStyle}>About</Link></li> {/* Liên kết đến trang giới thiệu */}
        </ul>
      </nav>
      <div style={searchContainerStyle}> {/* Thẻ div chứa ô tìm kiếm, áp dụng style searchContainerStyle */}
        <input type="text" placeholder="Search..." style={searchInputStyle} /> {/* Ô tìm kiếm với style searchInputStyle */}
      </div>
    </header>
  );
};

// Styling cho Header (inline CSS)
const headerStyle = {
  backgroundColor: '#2c3e50', // Màu nền của header
  color: '#fff', // Màu chữ trắng
  padding: '15px 30px', // Padding xung quanh nội dung header
  display: 'flex', // Hiển thị theo kiểu flex để dễ dàng căn chỉnh các phần tử bên trong
  justifyContent: 'space-between', // Căn các phần tử theo chiều ngang với không gian đều
  alignItems: 'center', // Căn các phần tử theo chiều dọc ở giữa
  boxShadow: '0 2px 5px rgba(0,0,0,0.2)', // Đổ bóng nhẹ cho header
};

const logoContainerStyle = {
  flex: 1, // Logo chiếm 1 phần không gian trong thanh header
};

const logoStyle = {
  fontSize: '28px', // Kích thước chữ logo
  fontWeight: 'bold', // Làm cho chữ logo đậm
  color: '#fff', // Màu chữ trắng
  textDecoration: 'none', // Không có gạch chân cho liên kết
};

const navStyle = {
  flex: 2, // Menu điều hướng chiếm 2 phần không gian trong thanh header
};

const navListStyle = {
  display: 'flex', // Danh sách điều hướng hiển thị theo chiều ngang
  listStyle: 'none', // Không có dấu chấm đầu dòng
  margin: 0, // Không có margin
  padding: 0, // Không có padding
  justifyContent: 'center', // Căn các mục menu vào giữa
};

const navItemStyle = {
  margin: '0 20px', // Khoảng cách giữa các mục menu
};

const linkStyle = {
  color: 'white', // Màu chữ trắng cho các liên kết
  textDecoration: 'none', // Không có gạch chân cho các liên kết
  fontSize: '18px', // Kích thước chữ
  transition: 'color 0.3s ease', // Hiệu ứng chuyển màu khi hover
};

const linkHoverStyle = {
  color: '#3498db', // Màu chữ khi hover
};

const searchContainerStyle = {
  flex: 1, // Ô tìm kiếm chiếm 1 phần không gian trong header
  display: 'flex', // Đặt ô tìm kiếm vào trong flex container
  justifyContent: 'flex-end', // Căn ô tìm kiếm về phía bên phải
};

const searchInputStyle = {
  padding: '10px', // Padding trong ô tìm kiếm
  borderRadius: '5px', // Bo tròn các góc của ô tìm kiếm
  border: 'none', // Không có viền
  width: '250px', // Chiều rộng của ô tìm kiếm
  fontSize: '16px', // Kích thước chữ trong ô tìm kiếm
};

export default Header; // Xuất component Header để sử dụng trong ứng dụng
