// src/components/FeaturedProducts.js
import axios from 'axios';
import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom'; // Đúng cách sử dụng useNavigate
import Slider from 'react-slick';

// Đảm bảo bạn đã cài slick-carousel và thêm CSS của nó
import "slick-carousel/slick/slick-theme.css";
import "slick-carousel/slick/slick.css";

const FeaturedProducts = () => {
  const [products, setProducts] = useState([]);
  const navigate = useNavigate(); // Dùng để điều hướng đến trang chi tiết sản phẩm

  // Cấu hình cho slick carousel
  const settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1,
    autoplay: true,
    autoplaySpeed: 3000, // Tốc độ tự động chuyển slide
    arrows: true,
  };

  useEffect(() => {
    // Lấy dữ liệu sản phẩm nổi bật từ backend
    const fetchFeaturedProducts = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/products/featured');
        setProducts(response.data);  // Lưu danh sách sản phẩm nổi bật vào state
      } catch (error) {
        console.error('Error fetching featured products', error);
      }
    };

    fetchFeaturedProducts();
  }, []);

  const handleProductClick = (productId) => {
    navigate(`/product/${productId}`); // Điều hướng đến trang chi tiết sản phẩm
  };

  return (
    <div className="featured-products">
      <h2>Featured Products</h2>
      <Slider {...settings}>
        {products.map((product) => (
          <div key={product._id} onClick={() => handleProductClick(product._id)}>
            <img src={product.imageUrl} alt={product.name} style={{ width: '100%', height: 'auto' }} />
            <h3>{product.name}</h3>
            <p>{product.price}</p>
          </div>
        ))}
      </Slider>
    </div>
  );
};

export default FeaturedProducts;
