import React, { useState } from 'react'; // Nháº­p useState tá»« React Ä‘á»ƒ quáº£n lÃ½ tráº¡ng thÃ¡i
import { Link, useHistory } from 'react-router-dom'; // Nháº­p Link vÃ  useHistory tá»« react-router-dom Ä‘á»ƒ Ä‘iá»u hÆ°á»›ng giá»¯a cÃ¡c trang trong á»©ng dá»¥ng

const Header = () => {
  const [searchQuery, setSearchQuery] = useState(''); // Quáº£n lÃ½ tráº¡ng thÃ¡i tÃ¬m kiáº¿m
  const history = useHistory(); // DÃ¹ng useHistory Ä‘á»ƒ Ä‘iá»u hÆ°á»›ng tá»›i trang tÃ¬m kiáº¿m

  // HÃ m xá»­ lÃ½ tÃ¬m kiáº¿m khi ngÆ°á»i dÃ¹ng nháº¥n Enter
  const handleSearch = (event) => {
    if (event.key === 'Enter' && searchQuery.trim() !== '') {
      // Chuyá»ƒn hÆ°á»›ng Ä‘áº¿n trang tÃ¬m kiáº¿m vá»›i tá»« khÃ³a
      history.push(`/search?q=${searchQuery}`);
    }
  };

  return (
    <header style={headerStyle}>
      <div style={logoContainerStyle}>
        <Link to="/" style={logoStyle}>
          <h1>BansToys</h1>
        </Link>
      </div>
      <nav style={navStyle}>
        <ul style={navListStyle}>
          <li style={navItemStyle}><Link to="/" style={linkStyle}>Home</Link></li>
          <li style={navItemStyle}><Link to="/products" style={linkStyle}>Product List</Link></li>
          <li style={navItemStyle}><Link to="/cart" style={linkStyle}>ðŸ›’</Link></li>
          <li style={navItemStyle}><Link to="/about" style={linkStyle}>About</Link></li>
        </ul>
      </nav>
      <div style={searchContainerStyle}>
        <input
          type="text"
          placeholder="Search..."
          style={searchInputStyle}
          value={searchQuery} // LiÃªn káº¿t vá»›i giÃ¡ trá»‹ trong state
          onChange={(e) => setSearchQuery(e.target.value)} // Cáº­p nháº­t khi ngÆ°á»i dÃ¹ng nháº­p
          onKeyDown={handleSearch} // Láº¯ng nghe sá»± kiá»‡n nháº¥n phÃ­m
        />
      </div>
    </header>
  );
};

